INSERT INTO public.comment(id, description, phone_id, user_id, grade, created)
VALUES ('7061c101-f756-438d-a840-312488ed5a29', 'Розблокування смартфона за допомогою Face ID працює швидко та точно навіть при різному освітленні або в незвичних умовах.', '2b50d9e8-21e1-40c1-a14d-adab17bd90c0', 'a1b93a99-599f-441c-85de-7deca0817cb3', 5, '2023-06-16');
INSERT INTO public.comment(id, description, phone_id, user_id, grade, created)
